      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let t of transactions">
        <td [class.text-success]="t.type === 'income'" [class.text-danger]="t.type === 'expense'">
          {{ t.type === 'income' ? 'Bev√©tel' : 'Kiad√°s' }}
        </td>
        <td>{{ t.amount }}</td>
        <td>{{ t.date }}</td>
      </tr>
    </tbody>
  </table>
</div>
