import { Component, OnInit } from '@angular/core';
import { WalletService } from '../../services/wallet.service';

interface Wallet {
  balance: number;
  income: number;
  expense: number;
}

@Component({
  selector: 'app-wallet',
  templateUrl: './wallet.component.html',
  styleUrls: ['./wallet.component.css']
})
export class WalletComponent implements OnInit {
  wallet: Wallet = {
    balance: 0,
    income: 0,
    expense: 0
  };

  amount: number = 0;

  constructor(private walletService: WalletService) {}

  ngOnInit(): void {
    this.loadWallet();
  }

  loadWallet() {
    this.wallet = {
      balance: this.walletService.getBalance(),
      income: this.walletService.getTotalIncome(),
      expense: this.walletService.getTotalExpense()
    };
  }

  addIncome() {
    if (this.amount > 0) {
      this.walletService.addIncome(this.amount);
      this.loadWallet();
      this.amount = 0;
    }
  }

  addExpense() {
    if (this.amount > 0) {
      this.walletService.addExpense(this.amount);
      this.loadWallet();
      this.amount = 0;
    }
  }
}
<div class="wallet-container">
  <div class="wallet-card">
    <h2>Pénztárca</h2>
    <h3 class="balance">Egyenleg: 
      <span [ngClass]="{ 'positive': wallet.balance >= 0, 'negative': wallet.balance < 0 }">
        {{ wallet.balance | currency:'HUF' }}
      </span>
    </h3>

    <div class="wallet-stats">
      <p><span class="income-label">Bevétel:</span> {{ wallet.income | currency:'HUF' }}</p>
      <p><span class="expense-label">Kiadás:</span> {{ wallet.expense | currency:'HUF' }}</p>
    </div>

    <div class="wallet-actions">
      <input type="number" [(ngModel)]="amount" class="amount-input" placeholder="Összeg">
      <div class="buttons">
        <button (click)="addIncome()" class="btn btn-success">+ Bevétel</button>
        <button (click)="addExpense()" class="btn btn-danger">- Kiadás</button>
      </div>
    </div>
  </div>
</div>
