import { Component, OnInit } from '@angular/core';
import { WalletService } from '../../services/wallet.service';

interface Wallet {
  balance: number;
  income: number;
  expense: number;
}

@Component({
  selector: 'app-wallet',
  templateUrl: './wallet.component.html',
  styleUrls: ['./wallet.component.css']
})
export class WalletComponent implements OnInit {
  wallet: Wallet = {
    balance: 0,
    income: 0,
    expense: 0
  };

  amount: number = 0;
  type: 'income' | 'expense' = 'income';

  constructor(private walletService: WalletService) {}

  ngOnInit(): void {
    this.loadWallet();
  }

  loadWallet() {
    this.wallet = {
      balance: this.walletService.getBalance(),
      income: this.walletService.getTotalIncome(),
      expense: this.walletService.getTotalExpense()
    };
  }

  addTransaction() {
    if (this.type === 'income') {
      this.walletService.addIncome(this.amount);
    } else {
      this.walletService.addExpense(this.amount);
    }
    this.loadWallet();
    this.amount = 0;
  }
}
<div class="wallet-container">
  <h2>Pénztárca</h2>

  <div class="balance-card">
    <h3>Egyenleg: {{ wallet.balance | currency:'HUF' }}</h3>
    <p>Bevétel: {{ wallet.income | currency:'HUF' }}</p>
    <p>Kiadás: {{ wallet.expense | currency:'HUF' }}</p>
  </div>

  <div class="actions">
    <input type="number" [(ngModel)]="amount" placeholder="Összeg" />
    <button (click)="addIncome()" class="btn btn-success">+ Bevétel</button>
    <button (click)="addExpense()" class="btn btn-danger">- Kiadás</button>
  </div>
</div>
