import { Component, OnInit } from '@angular/core';
import { AuthService } from '../../services/auth.service';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent implements OnInit {
  user: any;
  newPassword: string = '';

  constructor(private authService: AuthService) {}

  ngOnInit(): void {
    this.user = this.authService.getCurrentUser();
  }

  updateProfile() {
    if (this.newPassword.trim()) {
      this.authService.updatePassword(this.newPassword);
      this.newPassword = '';
      alert('‚úÖ Jelsz√≥ friss√≠tve!');
    }
    this.authService.updateName(this.user.name);
    alert('‚úÖ N√©v friss√≠tve!');
  }
}
<div class="profile-container">
  <div class="profile-card">
    <h2 class="title">üë§ Profilod</h2>

    <div class="info">
      <p><strong>N√©v:</strong> {{ user?.name }}</p>
      <p><strong>Email:</strong> {{ user?.email }}</p>
    </div>

    <div class="edit-section">
      <h3>‚úèÔ∏è Adatok m√≥dos√≠t√°sa</h3>
      <input type="text" [(ngModel)]="user.name" placeholder="√öj n√©v" />
      <input type="password" [(ngModel)]="newPassword" placeholder="√öj jelsz√≥" />
      <button (click)="updateProfile()">üíæ Ment√©s</button>
    </div>
  </div>
</div>
