import { Component, OnInit } from '@angular/core';
import { WalletService } from '../../services/wallet.service';
import { AuthService } from '../../services/auth.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  currentTime: string = '';
  balance: number = 0;
  income: number = 0;
  expense: number = 0;

  constructor(private walletService: WalletService,
    public authService: AuthService
  ) {}

  ngOnInit(): void {
    this.updateData();
    setInterval(() => this.updateData(), 1000);
  }

  updateData() {
    this.currentTime = new Date().toLocaleTimeString();
    this.balance = this.walletService.getBalance();
    this.income = this.walletService.getTotalIncome();
    this.expense = this.walletService.getTotalExpense();
  }
}
<div class="home-container">
  <section class="hero">
    <div class="hero-text">
      <h1>Üdvözöl a <span class="gradient">MoneyManager</span></h1>
      <p>Kezeld a pénzügyeidet okosan, egyszerűen és biztonságosan.  
         Kövesd a bevételeidet és kiadásaidat egy letisztult, modern felületen.</p>
      <div class="hero-actions">
        <a routerLink="wallets"><button class="btn primary">Pénztárcák megtekintése</button></a>
        <a routerLink="register" ><button class="btn ghost">Regisztráció</button></a>
      </div>
    </div>
    <div class="hero-image">
      <div class="glass-card balance">
        <h3>Aktuális egyenleg</h3>
        <h1>2 430 000 Ft</h1>
        <p class="muted">Frissítve: ma 10:42</p>
      </div>
      <div class="glass-card summary">
        <div class="summary-item">
          <span class="positive">+ 245 000 Ft</span>
          <p>Bevétel</p>
        </div>
        <div class="summary-item">
          <span class="negative">− 178 000 Ft</span>
          <p>Kiadás</p>
        </div>
      </div>
    </div>
  </section>

  <section class="features">
    <h2>Miért válassz minket?</h2>
    <div class="feature-grid">
      <div class="feature-card">
        <i class="material-icons">security</i>
        <h3>Biztonság</h3>
        <p>Banki szintű adatvédelem és titkosítás minden tranzakcióhoz.</p>
      </div>
      <div class="feature-card">
        <i class="material-icons">insights</i>
        <h3>Elemzések</h3>
        <p>Okos statisztikák, hogy mindig tudd, mire megy el a pénzed.</p>
      </div>
      <div class="feature-card">
        <i class="material-icons">devices</i>
        <h3>Bárhol, bármikor</h3>
        <p>Használd mobilon, tableten vagy gépen – mindig szinkronban marad.</p>
      </div>
    </div>
  </section>
</div>
