import { Component, OnInit } from '@angular/core';
import { WalletService, Transaction } from '../../services/wallet.service';

@Component({
  selector: 'app-transactions',
  templateUrl: './transactions.component.html',
  styleUrls: ['./transactions.component.css']
})
export class TransactionsComponent implements OnInit {
  transactions: Transaction[] = [];

  constructor(private walletService: WalletService) {}

  ngOnInit() {
    this.walletService.wallet$.subscribe(w => {
      this.transactions = [...w.transactions].reverse();
    });
  }
}
<div class="transactions-container">
  <h2>Tranzakciók</h2>
  <ul>
    <li *ngFor="let t of transactions" [class.income]="t.type==='income'" [class.expense]="t.type==='expense'">
      <span>{{ t.type === 'income' ? 'Bevétel' : 'Kiadás' }}</span>
      <span>{{ t.amount | currency:'HUF' }}</span>
      <small>{{ t.date | date:'short' }}</small>
    </li>
  </ul>
</div>
