import { Injectable } from '@angular/core';

export interface Transaction {
  type: 'income' | 'expense';
  amount: number;
  date: string;
}

@Injectable({
  providedIn: 'root'
})
export class WalletService {
  private balance = 0;
  private income = 0;
  private expense = 0;
  private transactions: Transaction[] = [];

  getBalance() {
    return this.balance;
  }

  getTotalIncome() {
    return this.income;
  }

  getTotalExpense() {
    return this.expense;
  }

  getTransactions() {
    return this.transactions;
  }

  addIncome(amount: number) {
    this.balance += amount;
    this.income += amount;
    this.transactions.push({
      type: 'income',
      amount,
      date: new Date().toLocaleString()
    });
  }

  addExpense(amount: number) {
    this.balance -= amount;
    this.expense += amount;
    this.transactions.push({
      type: 'expense',
      amount,
      date: new Date().toLocaleString()
    });
  }
}
